// Get chapter's audio file of a reciter
const axios = require('axios');

const API_BASE_URL = 'https://apis.quran.foundation/content/api/v4';
const ACCESS_TOKEN = '<YOUR_ACCESS_TOKEN>';
const CLIENT_ID = '<YOUR_CLIENT_ID>';

async function callApi(id, chapter_number, options = {}) {
  const response = await axios.get(
    `${API_BASE_URL}/chapter_recitations/${id}/${chapter_number}`,
    {
      headers: {
        'x-auth-token': ACCESS_TOKEN,
        'x-client-id': CLIENT_ID
      },
      params: options
    }
  );
  return response.data;
}

// Example usage
callApi("7", "1", {"segments":"true"})
  .then(data => console.log(data))
  .catch(err => console.error(err.response?.status, err.message));

# Get chapter's audio file of a reciter
import requests

API_BASE_URL = 'https://apis.quran.foundation/content/api/v4'
ACCESS_TOKEN = '<YOUR_ACCESS_TOKEN>'
CLIENT_ID = '<YOUR_CLIENT_ID>'

def call_api(id, chapter_number, segments=None):
    params = {k: v for k, v in {'segments': segments}.items() if v is not None}

    response = requests.get(
        f'{API_BASE_URL}/chapter_recitations/{id}/{chapter_number}',
        headers={
            'x-auth-token': ACCESS_TOKEN,
            'x-client-id': CLIENT_ID
        },
        params=params
    )
    response.raise_for_status()
    return response.json()

# Example usage
try:
    data = call_api("7", "1", segments="true")
    print(data)
except requests.HTTPError as e:
    print(f'Error {e.response.status_code}: {e}')

// Get chapter's audio file of a reciter
package main

import (
  "fmt"
  "io"
  "net/http"
  "net/url"
)

const API_BASE_URL = "https://apis.quran.foundation/content/api/v4"
const ACCESS_TOKEN = "<YOUR_ACCESS_TOKEN>"
const CLIENT_ID = "<YOUR_CLIENT_ID>"

func callApi(id string, chapter_number string, params map[string]string) (string, error) {
  path := fmt.Sprintf("/chapter_recitations/%s/%s", id, chapter_number)
  endpoint := API_BASE_URL + path
  if params != nil && len(params) > 0 {
    values := url.Values{}
    for k, v := range params {
      values.Set(k, v)
    }
    endpoint = endpoint + "?" + values.Encode()
  }

  req, err := http.NewRequest("GET", endpoint, nil)
  if err != nil {
    return "", err
  }
  req.Header.Set("x-auth-token", ACCESS_TOKEN)
  req.Header.Set("x-client-id", CLIENT_ID)

  resp, err := http.DefaultClient.Do(req)
  if err != nil {
    return "", err
  }
  defer resp.Body.Close()

  body, err := io.ReadAll(resp.Body)
  if err != nil {
    return "", err
  }
  return string(body), nil
}

// Example usage
func main() {
  params := map[string]string{"segments": "true"}
  data, err := callApi("7", "1", params)
  if err != nil {
    fmt.Println("Error:", err)
    return
  }
  fmt.Println(data)
}

