<?php

const API_BASE_URL = 'https://apis.quran.foundation/content/api/v4';
const ACCESS_TOKEN = '<YOUR_ACCESS_TOKEN>';
const CLIENT_ID = '<YOUR_CLIENT_ID>';

function callApi($id, $chapter_number, $params = []) {
  $path = sprintf("/chapter_recitations/%s/%s", $id, $chapter_number);
  $url = API_BASE_URL . $path;
  if (!empty($params)) {
    $url .= '?' . http_build_query($params);
  }
  $ch = curl_init($url);
  curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
  curl_setopt($ch, CURLOPT_CUSTOMREQUEST, 'GET');
  curl_setopt($ch, CURLOPT_HTTPHEADER, [
    'x-auth-token: ' . ACCESS_TOKEN,
    'x-client-id: ' . CLIENT_ID
  ]);

  $response = curl_exec($ch);
  if ($response === false) {
    throw new Exception(curl_error($ch));
  }
  curl_close($ch);
  return $response;
}

// Example usage
$params = ['segments' => 'true'];
$data = callApi("7", "1", $params);
echo $data;
?>

// Get chapter's audio file of a reciter
import java.io.IOException;
import java.util.HashMap;
import java.util.Map;
import okhttp3.HttpUrl;
import okhttp3.OkHttpClient;
import okhttp3.Request;
import okhttp3.RequestBody;
import okhttp3.Response;

public class Main {
  private static final String API_BASE_URL = "https://apis.quran.foundation/content/api/v4";
  private static final String ACCESS_TOKEN = "<YOUR_ACCESS_TOKEN>";
  private static final String CLIENT_ID = "<YOUR_CLIENT_ID>";

  public static String callApi(String id, String chapter_number, Map<String, String> params) throws IOException {
    String path = String.format("/chapter_recitations/%s/%s", id, chapter_number);
    HttpUrl.Builder urlBuilder = HttpUrl.parse(API_BASE_URL + path).newBuilder();
    if (params != null) {
      for (Map.Entry<String, String> entry : params.entrySet()) {
        urlBuilder.addQueryParameter(entry.getKey(), entry.getValue());
      }
    }
    String method = "GET";
    RequestBody requestBody = method.equals("GET") ? null : RequestBody.create(new byte[0]);
    Request request = new Request.Builder()
      .url(urlBuilder.build())
      .addHeader("x-auth-token", ACCESS_TOKEN)
      .addHeader("x-client-id", CLIENT_ID)
      .method(method, requestBody)
      .build();

    OkHttpClient client = new OkHttpClient();
    Response response = client.newCall(request).execute();
    return response.body().string();
  }

  public static void main(String[] args) throws Exception {
    Map<String, String> params = new HashMap<>();
    params.put("segments", "true");
    String data = callApi("7", "1", params);
    System.out.println(data);
  }
}

