$API_BASE_URL = "https://apis.quran.foundation/content/api/v4"
$ACCESS_TOKEN = "<YOUR_ACCESS_TOKEN>"
$CLIENT_ID = "<YOUR_CLIENT_ID>"

function Call-Api([string]$id, [string]$chapter_number, [hashtable]$params = @{}) {
  $path = "/chapter_recitations/$id/$chapter_number"
  $url = "$API_BASE_URL$path"
  if ($params.Count -gt 0) {
    $query = ($params.GetEnumerator() | ForEach-Object {
      "$($_.Key)=$([System.Uri]::EscapeDataString([string]$_.Value))"
    }) -join "&"
    $url = "$url?$query"
  }
  $headers = @{
    "x-auth-token" = $ACCESS_TOKEN
    "x-client-id" = $CLIENT_ID
  }

  Invoke-RestMethod -Method GET -Uri $url -Headers $headers
}

# Example usage
$params = @{ segments = "true" }
$data = Call-Api("7", "1", $params)
Write-Output $data

