# Get Quran verses in a specific script
import requests

API_BASE_URL = 'https://apis.quran.foundation/content/api/v4'
ACCESS_TOKEN = '<YOUR_ACCESS_TOKEN>'
CLIENT_ID = '<YOUR_CLIENT_ID>'

def call_api(script, chapter_number=None, juz_number=None, page_number=None):
    params = {k: v for k, v in {'chapter_number': chapter_number, 'juz_number': juz_number, 'page_number': page_number}.items() if v is not None}

    response = requests.get(
        f'{API_BASE_URL}/quran/verses/{script}',
        headers={
            'x-auth-token': ACCESS_TOKEN,
            'x-client-id': CLIENT_ID
        },
        params=params
    )
    response.raise_for_status()
    return response.json()

