    print(data)
except requests.HTTPError as e:
    print(f'Error {e.response.status_code}: {e}')

// Get Quran verses in a specific script
package main

import (
  "fmt"
  "io"
  "net/http"
  "net/url"
)

const API_BASE_URL = "https://apis.quran.foundation/content/api/v4"
const ACCESS_TOKEN = "<YOUR_ACCESS_TOKEN>"
const CLIENT_ID = "<YOUR_CLIENT_ID>"

func callApi(script string, params map[string]string) (string, error) {
  path := fmt.Sprintf("/quran/verses/%s", script)
  endpoint := API_BASE_URL + path
  if params != nil && len(params) > 0 {
    values := url.Values{}
    for k, v := range params {
      values.Set(k, v)
    }
    endpoint = endpoint + "?" + values.Encode()
  }

  req, err := http.NewRequest("GET", endpoint, nil)
  if err != nil {
    return "", err
  }
  req.Header.Set("x-auth-token", ACCESS_TOKEN)
  req.Header.Set("x-client-id", CLIENT_ID)

  resp, err := http.DefaultClient.Do(req)
  if err != nil {
    return "", err
  }
  defer resp.Body.Close()

  body, err := io.ReadAll(resp.Body)
  if err != nil {
    return "", err
  }
  return string(body), nil
}

// Example usage
func main() {
  params := map[string]string{"chapter_number": "1", "juz_number": "1", "page_number": "1"}
  data, err := callApi("uthmani", params)
  if err != nil {
    fmt.Println("Error:", err)
    return
  }
  fmt.Println(data)
}

