const string ACCESS_TOKEN = "<YOUR_ACCESS_TOKEN>";
const string CLIENT_ID = "<YOUR_CLIENT_ID>";

static async Task<string> CallApiAsync(string recitation_id, string ruku_number)
{
    var path = $@"/recitations/{recitation_id}/by_ruku/{ruku_number}";
