public class Main {
  private static final String API_BASE_URL = "https://apis.quran.foundation/content/api/v4";
  private static final String ACCESS_TOKEN = "<YOUR_ACCESS_TOKEN>";
  private static final String CLIENT_ID = "<YOUR_CLIENT_ID>";

