// Get chapter's audio file of a reciter
const axios = require('axios');

const API_BASE_URL = 'https://apis.quran.foundation/content/api/v4';
const ACCESS_TOKEN = '<YOUR_ACCESS_TOKEN>';
const CLIENT_ID = '<YOUR_CLIENT_ID>';

async function callApi(id, chapter_number, options = {}) {
  const response = await axios.get(
    `${API_BASE_URL}/chapter_recitations/7/1`,
    {
      headers: {
        'x-auth-token': ACCESS_TOKEN,
        'x-client-id': CLIENT_ID
      },
      params: options
    }
  );
  return response.data;
}

// Example usage
callApi("7", "1", {"segments":"true"})
  .then(data => console.log(data))
  .catch(err => console.error(err.response?.status, err.message));

# Get chapter's audio file of a reciter
import requests

API_BASE_URL = 'https://apis.quran.foundation/content/api/v4'
ACCESS_TOKEN = '<YOUR_ACCESS_TOKEN>'
CLIENT_ID = '<YOUR_CLIENT_ID>'

def call_api(id, chapter_number, segments=None):
    params = {k: v for k, v in {'segments': segments}.items() if v is not None}

    response = requests.get(
        f'{API_BASE_URL}/chapter_recitations/7/1',
        headers={
            'x-auth-token': ACCESS_TOKEN,
            'x-client-id': CLIENT_ID
        },
        params=params
    )
    response.raise_for_status()
    return response.json()

# Example usage
try:
    data = call_api("7", "1", segments="true")
    print(data)
except requests.HTTPError as e:
    print(f'Error {e.response.status_code}: {e}')

