import requests

API_BASE_URL = 'https://apis.quran.foundation/content/api/v4'
ACCESS_TOKEN = '<YOUR_ACCESS_TOKEN>'
CLIENT_ID = '<YOUR_CLIENT_ID>'

def call_api(from=None, to=None, language=None):
    params = {k: v for k, v in {'from': from, 'to': to, 'language': language}.items() if v is not None}

    response = requests.get(
        f'{API_BASE_URL}/verses/by_range',
        headers={
